var obj = {};

function bindDomWithObject(options) {
  var dom = document.getElementById(options.id); // è·å–dom id
  var obj = options.obj; // éœ€è¦ç»‘å®šçš„obj
  var prop = options.prop; // éœ€è¦ç»‘å®šçš„obj çš„å±æ€§
  var callback = options.callback; // ç»‘å®šæˆåŠŸåè°ƒç”¨
  var type = options.type; // ç»‘å®šçš„äº‹ä»¶ç±»å‹
  var updated = options.updated; // æ›´æ–°æˆåŠŸåè°ƒç”¨

  Object.defineProperty(obj, prop, {
    get: function () {
      return dom.value;
    },
    set: function (value) {
      dom.value = value;
      localStorage.setItem(prop, value);
    },
    configurable: true,
  });

  dom.addEventListener(type, function () {
    obj[prop] = obj[prop];
    if (typeof updated === "function") {
      updated(obj, prop, dom); // ä¼ å…¥å¯¹è±¡ï¼Œ ä¿®æ”¹çš„å±æ€§ï¼Œ ä»¥åŠdomèŠ‚ç‚¹
    }
  });

  if (typeof callback === "function") {
    callback(options, obj, dom);
  }
}

function propInit(id, type) {
  bindDomWithObject({
    id: id,
    obj: obj,
    prop: id,
    type: type,
    callback: function (options, obj, dom) {
      if (!API.isEmpty(localStorage.getItem(id))) {
        obj[options.prop] = localStorage.getItem(id);
      }
    },
  });
  console.log(`${id} binded successfully`);
}

function propChange(id, changeFn) {
  bindDomWithObject({
    id: id,
    obj: obj,
    prop: id,
    type: "change",
    updated: changeFn,
  });
  changeFn();
}

function checkedInit(obj) {
  if (localStorage.getItem(obj.id) === "true") {
    obj.checked = true;
  } else {
    obj.checked = false;
  }
  console.log(`${obj.id} binded successfully`);
}

function checkedChange(obj, YesFn, NoFn) {
  if (obj.checked && obj.checked === true) {
    localStorage.setItem(obj.id, "true");
    YesFn();
  } else {
    localStorage.setItem(obj.id, "false");
    NoFn();
  }
  obj.addEventListener("click", function () {
    if (obj.checked === true) {
      localStorage.setItem(obj.id, "true");
      YesFn();
    } else {
      localStorage.setItem(obj.id, "false");
      NoFn();
    }
  });
}

